<?php	function insertEvent($event, $src, $dest)	{			require_once 'db.php';		$link = mysql_connect(DB_HOST, DB_USER, DB_PASS)			 or die('Could not connect: ' . mysql_error());		mysql_select_db(DB_NAME) or die('Could not select database ' . DB_NAME);		$sql= "INSERT INTO events (event, src, dest) VALUES('" . $event . "','" . $src . "','" . $dest . "')";		//echo '<br>' . $sql;		$result = mysql_query($sql) or die('Query failed: ' . mysql_error());		return $result;	}?>